import React, { PureComponent } from 'react';

export default class AnalyzeSpending extends PureComponent {
  makeSpentCards = spendObj => {
    const arr = spendObj;
    const analyzeSection = document.getElementById('analyze');
    analyzeSection.innerHTML = '';
    let cards = [];
    for (let i = 0; i < arr.length; i++) {
      cards.push(`<div id="${i}-purchase">
                <h2>${arr.purchaseDate}</h2>
                <h2>${arr.category}</h2>
                <p>${arr.amount}</p>
            </div>`);
    }
  };

  render() {
    return (
      <div className="analyze" id="analyze">
        <h1>Analyze Spending Record</h1>
        <h2>Charts here:</h2>
      </div>
    );
  }
}
